import React from "react";
import "./App.css";
import { Route, Switch } from "react-router-dom";
import QuestionListPage from "./pages/QuestionListPage";
import NewQuestionPage from "./pages/NewQuestionPage";
import QuestionsProvider from './context/QuestionsProvider';

// não é legal colocar o QuestionsContext.Provider value={{}} aqui no App
//o ideal é criar um componente para o Provider (será um container, para 'embrulhar' o que está no return do App)
// embrulhar com o Question
function App() {
  return (
    <QuestionsProvider>
      <main className="App">
        <header className="main-header">
          <section className="main-header-title">
            <h3>Trybe Questions</h3>
          </section>
        </header>
        <section className="main-section">
          <Switch>
            <Route exact path="/new-question" component={NewQuestionPage} />
            <Route exact path="/" component={QuestionListPage} />
          </Switch>
        </section>
      </main>
    </QuestionsProvider>
  );
}

export default App;
