import React, { useState } from "react";

function QuestionCard({ question }) {

  const [claps, setClaps] = useState(0);

  return (
    <section className="question-card">
      <header className="question-card-header">
        <img
          className="question-card-image"
          src="https://img1.gratispng.com/20180329/izw/kisspng-crash-bandicoot-n-sane-trilogy-crash-bandicoot-w-crash-bandicoot-5abc9835bb63c3.5802666415223091737676.jpg"
          alt=""
        />
        {question.name}
      </header>
      <p className="question-card-body">{question.question}</p>

      <p className="question-card-claps">
        <span
          role="img"
          aria-label="increase-claps"
          onClick={() => setClaps((prevClaps) => prevClaps + 1)}
        >
          üëè
        </span>
        {claps} Palmas
      </p>
    </section>
  );
}

export default QuestionCard;
