import React, { useState, useContext } from 'react';
import QuestionsContext from '../context/QuestionsContext';
import { useHistory } from 'react-router-dom'

function NewQuestionPage() {

  const [question, setQuestion] = useState('');
  const [name, setName] = useState('');
  const { addNewQuestion } = useContext(QuestionsContext);

  //enviar o form
  function handleSubmit(event) {
    // evitar o reload da página
    event.preventDefault();

    // preparar o objeto question
    const newQuestion = {
      id: Math.random().toString().substr(2,9), // id randômico, transformado em string e que pega 9 dígitos depois da segunda casa decimal
      name, // object shorthand
      question, // object shorthand
      claps: 0,
    }

    // enviar o objeto para o estado global
    addNewQuestion(newQuestion);

    // redireciona para a página inicial

    // enviar o form
  }

  return (
    <section className="question-section">
      <form onSubmit={ handleSubmit }>
        <label htmlFor="question">
          Digite a sua pergunta:
          <textarea 
            id="question"
            cols="30"
            name="question"
            // (event) => setQuestion(event.target.value)
            onChange={(event) => setQuestion(event.target.value)}
            placeholder="Escreva aqui sua pergunta"
            rows="10"
            value={ question }
          />
        </label>
        <label htmlFor="user">
          Digite o seu nome:
          <input
            id="user"
            name="name"
            onChange={(event) => setName(event.target.value)}
            placeholder="Escreva seu nome..."
            type="text"
            value={ name }
          />
        </label>
        <button type="submit">Enviar</button>
      </form>
    </section>
  );
}

export default NewQuestionPage;
