import React from 'react';
import QuestionCard from '../components/QuestionCard';

// o history agora chegará como props no parâmetro da função
// retira o class, extends e component acrescenta o function
function QuestionListPage({history}) {
  // remove 
  // function handleClickNextPage() {
  //   const { history } = this.props;
  //   history.push('/new-question');
  // }

  const questions = [
    {
      id: 1,
      name: "Hermione",
      question: "Como fazer a poção polissuco?",
      claps: 7,
    },
  ];

  return (
    <section>
      {questions.map((question, index) => (
        <QuestionCard key={ index } question={ question } />
      ))}
      {/* retira o this antes da função handleClickNextPage
      depois, como a função só chama o history.push()
      fiz a função anônima dentro do onClick */}
      <button type="button" onClick={ () => history.push('/new-question') }>
        Nova Pergunta
      </button>
    </section>
  )
}

export default QuestionListPage;
